var pi;
let digits = [];
let index = 0;

function setup() {
    createCanvas(500,500);
    pi = "";
    
    let sdigits = pi.split("");
    digits = sdigits;
    
    background(0);
    translate(width/2, height/2);
    noFill();
    stroke(255);
    ellipse(0,0,500,500);
}

function draw() {
    translate(width/2, height/2);
    let digit = digits[index.toString()];
    let t = index + 1;
    let nexDigit = digits[t.toString()];
    index++;

    let diff = TWO_PI/10;

    let a1 = map(Number(digit),0,10,0,TWO_PI) + random(-diff, diff);
    let a2 = map(Number(nexDigit),0,10,0,TWO_PI) + random(-diff, diff);

    let x1 = 250*cos(a1);
    let y1 = 250*sin(a1);

    let x2 = 250*cos(a2);
    let y2 = 250*sin(a2);

    stroke(255, 50);
    line(x1,y1,x2,y2);
}